// ChatRoom.tsx

import React, { useState } from 'react';
import Message from './Message';

interface ChatRoomProps {
  username: string;
  onLogout: () => void;
}

const ChatRoom: React.FC<ChatRoomProps> = ({ username, onLogout }) => {
  const [messages, setMessages] = useState<string[]>([]);
  const [newMessage, setNewMessage] = useState<string>('');

  const sendMessage = () => {
    if (newMessage.trim() !== '') {
      setMessages([...messages, newMessage]);
      setNewMessage('');
    }
  };

  const handleUpvote = (index: number) => {
    // Implement upvoting logic here
  };

  const handleDownvote = (index: number) => {
    // Implement downvoting logic here
  };

  return (
    <div>
      <h2>Welcome, {username}!</h2>
      <button onClick={onLogout}>Logout</button>
      <div>
        {messages.map((message, index) => (
          <Message
            key={index}
            message={message}
            onUpvote={() => handleUpvote(index)}
            onDownvote={() => handleDownvote(index)}
          />
        ))}
      </div>
      <div>
        <input
          type="text"
          placeholder="Type your message here"
          value={newMessage}
          onChange={(e) => setNewMessage(e.target.value)}
        />
        <button onClick={sendMessage}>Send</button>
      </div>
    </div>
  );
};

export default ChatRoom;
