import React from 'react';
import './Message.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faThumbsUp, faThumbsDown } from '@fortawesome/free-solid-svg-icons';

export interface MessageProps {
  message: {
    _id: string;
    username: string;
    content: string;
    votes: number;
    userVote?: 'upvote' | 'downvote';
  };
  onVote: (messageId: string, voteType: 'upvote' | 'downvote') => void;
}

const Message: React.FC<MessageProps> = ({ message, onVote }) => {
  const handleVote = (voteType: 'upvote' | 'downvote') => {
    onVote(message._id, voteType);
  };

  return (
    <div className="message">
      <p className="message-content"><strong>{message.username}</strong>: {message.content}</p>
      <div className="message-actions">
        <button
          className={`vote-btn upvote-btn ${message.userVote === 'upvote' ? 'active' : ''}`}
          onClick={() => handleVote('upvote')}
          disabled={message.userVote === 'upvote'} // Disable button if user already upvoted
        >
          <FontAwesomeIcon icon={faThumbsUp} />
        </button>
        <span className="votes">{message.votes}</span>
        <button
          className={`vote-btn downvote-btn ${message.userVote === 'downvote' ? 'active' : ''}`}
          onClick={() => handleVote('downvote')}
          disabled={message.userVote === 'downvote'} // Disable button if user already downvoted
        >
          <FontAwesomeIcon icon={faThumbsDown} />
        </button>
      </div>
    </div>
  );

};

export default Message;
