import React, { useState, useEffect } from 'react';
import axios from 'axios';

interface Message {
  id: number;
  content: string;
  votes: number;
}

const App: React.FC = () => {
  const [username, setUsername] = useState<string>('');
  const [password, setPassword] = useState<string>('');
  const [isLoggedIn, setIsLoggedIn] = useState<boolean>(false);
  const [messages, setMessages] = useState<Message[]>([]);
  const [newMessage, setNewMessage] = useState<string>('');

  useEffect(() => {
    // Fetch messages from the server
    axios.get('http://127.0.0.1:5000/messages')
      .then(response => {
        setMessages(response.data);
      })
      .catch(error => {
        console.error('Error fetching messages:', error);
      });
  }, []);

  const handleLogin = () => {
    // Send login credentials to the server
    axios.post('http://127.0.0.1:5000/login', 
      { username, password },
      {
        headers: {
          'Content-Type': 'application/json',
          'Access-Control-Allow-Origin': '*'
        }
      }
    )
      .then(response => {
        setIsLoggedIn(true);
      })
      .catch(error => {
        console.error('Login error:', error);
      });
  };

  const handleSendMessage = () => {
    // Send new message to the server
    axios.post('http://127.0.0.1:5000/messages', 
      { content: newMessage },
      {
        headers: {
          'Content-Type': 'application/json',
          'Access-Control-Allow-Origin': '*'
        }
      })
      .then(response => {
        setNewMessage('');
        setMessages([...messages, response.data]);
      })
      .catch(error => {
        console.error('Error sending message:', error);
      });
  };

  const handleVote = (id: number, type: 'up' | 'down') => {
    // Send vote to the server
    axios.put(`http://127.0.0.1:5000/messages/${id}/${type}`,
      {
        headers: {
          'Content-Type': 'application/json',
          'Access-Control-Allow-Origin': '*'
        }
      })
      .then(response => {
        const updatedMessages = messages.map(message => {
          if (message.id === id) {
            return { ...message, votes: response.data.votes };
          }
          return message;
        });
        setMessages(updatedMessages);
      })
      .catch(error => {
        console.error('Error voting:', error);
      });
  };

  return (
    <div>
      {isLoggedIn ? (
        <div>
          <h1>Welcome, {username}!</h1>
          <div>
            <input type="text" value={newMessage} onChange={e => setNewMessage(e.target.value)} />
            <button onClick={handleSendMessage}>Send</button>
          </div>
          <ul>
            {messages.map(message => (
              <li key={message.id}>
                <span>{message.content}</span>
                <button onClick={() => handleVote(message.id, 'up')}>Upvote</button>
                <button onClick={() => handleVote(message.id, 'down')}>Downvote</button>
                <span>Votes: {message.votes}</span>
              </li>
            ))}
          </ul>
        </div>
      ) : (
        <div>
          <h1>Login</h1>
          <input type="text" placeholder="Username" value={username} onChange={e => setUsername(e.target.value)} />
          <input type="password" placeholder="Password" value={password} onChange={e => setPassword(e.target.value)} />
          <button onClick={handleLogin}>Login</button>
        </div>
      )}
    </div>
  );
};

export default App;
