// ChatRoom.tsx
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Message, { MessageProps } from './Message';
import { useLocation, Navigate } from 'react-router-dom';
import './ChatRoom.css';

const Chatroom: React.FC = () => {
  const [messageInput, setMessageInput] = useState('');
  const [messages, setMessages] = useState<MessageProps[]>([]);
  const location = useLocation();
  const username = location.state?.username;
  const token = localStorage.getItem('token');
  const [validToken, setValidToken] = useState(true); // State to track token validity

  useEffect(() => {
    // Function to verify token validity
    const verifyToken = async () => {
      try {
        await axios.get('http://127.0.0.1:5000/api/verifyToken', {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        setValidToken(true);
        if (username) {
          fetchMessageHistory(username);
        }
      } catch (error) {
        console.error('Invalid token:', error);
        setValidToken(false);
      }
    };

    if (token) {
      verifyToken();
    }
  }, [username, token]);

  const fetchMessageHistory = async (username: string) => {
    try {
      const response = await axios.get('http://127.0.0.1:5000/api/messages', { params: { username: username } });
      setMessages(response.data);
    } catch (error) {
      console.error('Error fetching message history:', error);
    }
  };

  const sendMessage = async () => {
    try {
      // Check if message is not empty
      if (!messageInput.trim()) {
        alert('Message can\'t be empty');
        return;
      }
      const response = await axios.post('http://127.0.0.1:5000/api/messages', {  username: username, content: messageInput });
      setMessages([...messages, response.data]);
      setMessageInput('');
    } catch (error) {
      console.error('Error sending message:', error);
    }
  };

  const handleVote = async (messageId: string, voteType: 'upvote' | 'downvote', userVote: 'upvote' | 'downvote' | 'none') => {
    try {
      await axios.put(`http://127.0.0.1:5000/api/messages/${messageId}`, { voteType: voteType, userVote: userVote });
      // Update the local state to reflect the change in votes
      const updatedMessages = messages.map((message: any) => {
        if (message._id === messageId) {

          let upvoteInc = 0;
          let downvoteInc = 0;

          if (userVote === 'upvote') {
              if (voteType === 'downvote') {
                  downvoteInc = 1;
              }
              upvoteInc = -1;
          } else if (userVote === 'downvote') {
              if (voteType === 'upvote') {
                  upvoteInc = 1;
              }
              downvoteInc = -1;
          } else {
              if (voteType === 'upvote') {
                  upvoteInc = 1;
              } else {
                  downvoteInc = 1;
              }
          }

          return {
            ...message,
            upvotes: message.upvotes + upvoteInc,
            downvotes: message.downvotes + downvoteInc,
            userVote: userVote === voteType ? 'none' : voteType
          };
        }
        return message;
      });
      setMessages(updatedMessages);
    } catch (error) {
      console.error('Error voting on message:', error);
    }
  };

  if (!token || !validToken) {
    return <Navigate to="/login" />;
  }

  return (
    <div className="chatroom-container">
      <h2>Chatroom</h2>
      <div className="messages">
        {messages.map((message: any) => (
          <Message key={message._id} message={message} onVote={handleVote} />
        ))}
      </div>
      <div className="message-input">
        <input type="text" value={messageInput} onChange={(e) => setMessageInput(e.target.value)} />
        <button onClick={sendMessage}>Send</button>
      </div>
    </div>
  );
};

export default Chatroom;
