import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Message, { MessageProps } from './Message';
import { useLocation } from 'react-router-dom';
import './ChatRoom.css';

const Chatroom: React.FC = () => {
  const [messageInput, setMessageInput] = useState('');
  const [messages, setMessages] = useState<MessageProps[]>([]);
  const location = useLocation();
  const username = location.state.username

  useEffect(() => {
    fetchMessageHistory();
  }, []);

  const fetchMessageHistory = async () => {
    try {
      const response = await axios.get('http://127.0.0.1:5000/api/messages');
      setMessages(response.data);
    } catch (error) {
      console.error('Error fetching message history:', error);
    }
  };

  const sendMessage = async () => {
    try {
      // Check if message is not empty
      if (!messageInput.trim()) {
        alert('Message can\'t be empty');
        return;
      }
      const response = await axios.post('http://127.0.0.1:5000/api/messages', {  username: username, content: messageInput });
      setMessages([...messages, response.data]);
      setMessageInput('');
    } catch (error) {
      console.error('Error sending message:', error);
    }
  };

  const handleVote = async (messageId: string, voteType: 'upvote' | 'downvote') => {
    try {
      await axios.put(`http://127.0.0.1:5000/api/messages/${messageId}/${voteType}`);
      // Update the local state to reflect the change in votes
      const updatedMessages = messages.map((message: any) => {
        if (message._id === messageId) {
          return {
            ...message,
            votes: voteType === 'upvote' ? message.votes + 1 : message.votes - 1,
            userVote: voteType
          };
        }
        return message;
      });
      setMessages(updatedMessages);
    } catch (error) {
      console.error('Error voting on message:', error);
    }
  };

  return (
    <div className="chatroom-container">
      <h2>Chatroom</h2>
      <div className="messages">
        {messages.map((message: any) => (
          <Message key={message._id} message={message} onVote={handleVote} />
        ))}
      </div>
      <div className="message-input">
        <input type="text" value={messageInput} onChange={(e) => setMessageInput(e.target.value)} />
        <button onClick={sendMessage}>Send</button>
      </div>
    </div>
  );
};

export default Chatroom;
