import React from 'react';
import './Message.css';

export interface MessageProps {
  message: {
    _id: string;
    username: string;
    content: string;
    votes: number;
  };
  onVote: (messageId: string, voteType: 'upvote' | 'downvote') => void;
}

const Message: React.FC<MessageProps> = ({ message, onVote }) => {
  const handleVote = (voteType: 'upvote' | 'downvote') => {
    onVote(message._id, voteType);
  };

  return (
    <div className="message">
      <p className="message-content"><strong>{message.username}</strong>: {message.content}</p>
      <div className="message-actions">
        <button className="vote-btn upvote-btn" onClick={() => handleVote('upvote')}>
          <span role="img" aria-label="Like">&#128077;</span>
        </button>
        <span className="votes">{message.votes}</span>
        <button className="vote-btn downvote-btn" onClick={() => handleVote('downvote')}>
          <span role="img" aria-label="Dislike">&#128078;</span>
        </button>
      </div>
    </div>
  );

};

export default Message;
