import React from 'react';
import axios from 'axios';

interface MessageProps {
  message: {
    _id: string;
    content: string;
    votes: number;
  };
  onVote: (messageId: string, voteType: 'upvote' | 'downvote') => void;
}

const Message: React.FC<MessageProps> = ({ message, onVote }) => {
  const handleVote = (voteType: 'upvote' | 'downvote') => {
    onVote(message._id, voteType);
  };

  return (
    <div className="message">
      <p>{message.content}</p>
      <p>Votes: {message.votes}</p>
      <button onClick={() => handleVote('upvote')}>Upvote</button>
      <button onClick={() => handleVote('downvote')}>Downvote</button>
    </div>
  );
};

export default Message;
