// SignUp.tsx
import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios, { AxiosError } from 'axios';

const SignUp: React.FC = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleSignUp = async () => {
    try {
        // Check if username and password are not empty
        if (!username.trim() || !password.trim()) {
            alert('Username and Password can\'t be empty');
            return;
        }
        const response = await axios.post('http://127.0.0.1:5000/api/signup', { username, password });
        alert('Signup was successful! Please log in to your account to enter the chatroom.')
        navigate('/login');
      // Handle success (redirect, show success message, etc.)
    } catch (error) {
        if (error instanceof AxiosError) {
            if (error.response && error.response.data && error.response.data.message) {
                alert(error.response.data.message);
            }
        }
        console.error('Error signing up:', error);
    }
  };

  return (
    <div className="signup-container">
      <h2>Sign Up</h2>
      <input className="input-field" type="text" placeholder="Username" value={username} onChange={(e) => setUsername(e.target.value)} />
      <input className="input-field" type="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} />
      <button className="signup-button" onClick={handleSignUp}>Sign Up</button>
      <p className="login-link">Already have an account? <Link to="/login">Login</Link></p>
    </div>
  );
};

export default SignUp;
