// Login.tsx
import React, { useState } from 'react';
import axios, { AxiosError } from 'axios';
import { useNavigate } from 'react-router-dom';

const Login: React.FC<{ setToken: (token: string) => void }> = ({ setToken }) => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleLogin = async () => {
    try {
      // Check if username and password are not empty
      if (!username.trim() || !password.trim()) {
        alert('Username and Password can\'t be empty');
        return;
      }
      const response = await axios.post('http://127.0.0.1:5000/api/login', { username, password });
      const { access_token } = response.data;
      localStorage.setItem('token', access_token);
      setToken(access_token); 
      navigate('/chatroom', { state: { username } });
      // Handle success (redirect, store token in local storage, etc.)
    } catch (error) {
      if (error instanceof AxiosError) {
        if (error.response && error.response.data && error.response.data.message) {
          alert(error.response.data.message);
        }
      }
      console.error('Error logging in:', error);
      // Handle error (show error message, etc.)
    }
  };

  return (
    <div className="signup-container">
      <h2>Login</h2>
      <div className="form-group">
        <input type="text" placeholder="Username" value={username} onChange={(e) => setUsername(e.target.value)} />
      </div>
      <div className="form-group">
        <input type="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} />
      </div>
      <button className="signup-btn" onClick={handleLogin}>Login</button>
      <p>Don't have an account? <Link to="/signup">Sign Up</Link></p>
    </div>
  );
};

export default Login;
